local PS = game:GetService("Players") local Player = PS.LocalPlayer local Character = Player.Character or Player.CharacterAdded:Wait() local RS = game:GetService("ReplicatedStorage") local CE = RS:WaitForChild("CharacterEvents") local R = game:GetService("RunService") local BeingHeld = Player:WaitForChild("IsHeld") local PlayerScripts = Player:WaitForChild("PlayerScripts") --[[ Remotes ]] local StruggleEvent = CE:WaitForChild("Struggle") --[[ Anti-Explosion ]] workspace.DescendantAdded:Connect(function(v) if v:IsA("Explosion") then v.BlastPressure = 0 end end) --[[ Anti-grab ]] BeingHeld.Changed:Connect(function(C) if C == true then local char = Player.Character if BeingHeld.Value == true then local Event; Event = R.RenderStepped:Connect(function() if BeingHeld.Value == true then char["HumanoidRootPart"].AssemblyLinearVelocity = Vector3.new() StruggleEvent:FireServer(Player) elseif BeingHeld.Value == false then Event:Disconnect() end end) end end end) Player.CharacterAdded:Connect(function(char) char:FindFirstChildOfClass("Humanoid").Changed:Connect(function(C) if C == "Sit" and Humanoid.Sit == true then if Humanoid.SeatPart ~= nil and tostring(Humanoid.SeatPart.Parent) == "CreatureBlobman" then elseif Humanoid.SeatPart == nil then Humanoid:SetStateEnabled(Enum.HumanoidStateType.Jumping, true) Humanoid.Sit = false end end end) end) local bodyvel_Name = "FlingVelocity" local userinputs = game:GetService("UserInputService") local w = game:GetService("Workspace") local r = game:GetService("RunService") local d = game:GetService("Debris") local strength = 500 local fling = true local ScreenGui = Instance.new("ScreenGui") local SuperFling = Instance.new("TextButton") ScreenGui.Parent = game.Players.LocalPlayer:WaitForChild("PlayerGui") ScreenGui.ZIndexBehavior = Enum.ZIndexBehavior.Sibling SuperFling.Name = "SuperFling" SuperFling.Parent = ScreenGui SuperFling.BackgroundColor3 = Color3.fromRGB(18, 255, 6) SuperFling.BorderColor3 = Color3.fromRGB(0, 0, 0) SuperFling.BorderSizePixel = 2 SuperFling.Position = UDim2.new(0.918388188, 0, 0.529877484, 0) SuperFling.Size = UDim2.new(0.0603257343, 0, 0.085752748, 0) SuperFling.Font = Enum.Font.SciFi SuperFling.Text = "Super Fling: ON!" SuperFling.TextColor3 = Color3.fromRGB(0, 0, 0) SuperFling.TextSize = 7 SuperFling.TextStrokeColor3 = Color3.fromRGB(18, 255, 6) SuperFling.TextStrokeTransparency = 0.000 SuperFling.TextWrapped = true SuperFling.MouseButton1Up:Connect(function() if fling then fling = false SuperFling.Text = "Super Fling: OFF!" SuperFling.BackgroundColor3 = Color3.fromRGB(255, 0, 0) SuperFling.TextStrokeColor3 = Color3.fromRGB(255, 0, 0) else fling = true SuperFling.Text = "Super Fling: ON!" SuperFling.BackgroundColor3 = Color3.fromRGB(18, 255, 6) SuperFling.TextStrokeColor3 = Color3.fromRGB(18, 255, 6) end end) w.ChildAdded:Connect(function(model) if model.Name == "GrabParts" then local part_to_impulse = model["GrabPart"]["WeldConstraint"].Part1 if part_to_impulse then print("Part found!") local inputObj local velocityObj = Instance.new("BodyVelocity", part_to_impulse) model:GetPropertyChangedSignal("Parent"):Connect(function() if not model.Parent then if fling then print("Launched!") velocityObj.MaxForce = Vector3.new(math.huge, math.huge, math.huge) velocityObj.Velocity = workspace.CurrentCamera.CFrame.lookVector * strength d:AddItem(velocityObj, 1) else velocityObj.MaxForce = Vector3.new(0,0,0) d:AddItem(velocityObj, 1) print("Cancel Launch!") end end end) end end end)
